import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import {useState} from 'react';

function App() {
  const [amount, setAmount] = useState();
  const [people, setPeople] = useState();
  const [isForm1Visible, setIsForm1Visible] = useState(true);

  const handleSubmit = event => {
    event.preventDefault();

    setAmount(event.target.total.value);
    setPeople(event.target.people.value);
    setIsForm1Visible(false);
  }
  return (
    <div className="App">
      <div className="Main">
        <h2>Bill split</h2>
        {/* Form for total & names */}
        <Form onSubmit={handleSubmit} style={{display: isForm1Visible ? 'visible' : 'hidden'}}>
          <Form.Group className="mb-3">
            <Form.Label>How much is the total cost?</Form.Label>
            <Form.Control type="number" step="0.01" placeholder="XX.YY" name="total" />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Who was involved today?</Form.Label>
            <Form.Control type="text" placeholder="Jane, Jack, etc." name="people" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>

        {/* Form for specific items */}

        <br/>

        <h2>Results</h2>
        <ul>
          <li>Amount: {amount}</li>
          <li>People: {people}</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
