import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import {useState} from 'react';

function App() {
  const [amount, setAmount] = useState();
  const [people, setPeople] = useState();
  const peopleList = [];
  const [isForm1Visible, setIsForm1Visible] = useState(true);
  const [isForm2Visible, setIsForm2Visible] = useState(true);

  const handleForm1Submit = event => {
    event.preventDefault();

    setAmount(event.target.total.value);
    setPeople(event.target.people.value);
    setIsForm1Visible(false);
    setIsForm2Visible(true);

    console.log({people});
  }

  const Input = () => {
    return <Form.Group className="mb-3"><Form.Control placeholder="Your input here" /></Form.Group>;
  };

  const [inputList, setInputList] = useState([]);

  const onAddBtnClick = event => {
    setInputList(inputList.concat(<Input key={inputList.length} />));
  };

  return (
    <div className="App">
      <div className="Main">
        <h1>Bill split</h1>
        {/* Form for total & names */}
        <Form onSubmit={handleForm1Submit} style={{display: isForm1Visible ? '' : 'none'}}>
          <Form.Group className="mb-3">
            <Form.Label>How much is the total cost?</Form.Label>
            <Form.Control type="number" step="0.01" placeholder="XX.YY" name="total" />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Who was involved today?</Form.Label>
            <Form.Control type="text" placeholder="Jane, Jack, etc." name="people" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit total and people
          </Button>
        </Form>

        <br/>

        {/* Form for specific items */}
        <Form onSubmit={handleForm1Submit} style={{display: isForm2Visible ? '' : 'none'}}>
          <Button variant="primary" onClick={onAddBtnClick}>Add input</Button>
          {inputList}
          {/* <Form.Control as="select" multiple value={options} onChange={onSelectedOptionsChange}>
            {options.map(options => (
              <option key={option.name} value={option.value}>
                {option.name}
              </option>
            ))}
          </Form.Control> */}
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>

        <br/>

        <h4>Results</h4>
        <ul>
          <li>Amount: {amount}</li>
          <li>People: {peopleList}</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
